override fun onOptionsItemSelected(item: MenuItem): Boolean {
    when (item.itemId) {
        android.R.id.home -> {
            finish()
            return true
        }
    }
    return true
}

fun setupRecyclerView() {
        val linearLayoutManager = LinearLayoutManager(
                this,
                LinearLayoutManager.VERTICAL,
                false
        )

        recyclerView.layoutManager = linearLayoutManager
        adapter = MainAdapter(itemModels)
        recyclerView.adapter = adapter
    }

interface MainAdapterListener { fun onItemClick(position: Int) }
class MainAdapter(val items: MutableList<ItemModel>, val listener: MainAdapterListener)
: RecyclerView.Adapter<MainAdapter.ViewHolder>() {
override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ViewHolder { val itemView = LayoutInflater.from(parent?.context) .inflate(R.layout.item_list, parent, false) return ViewHolder(itemView) }
override fun onBindViewHolder(holder: ViewHolder, position: Int) { val item = items[position]
holder?.titleTextView.text = item.title holder?.contentTextView.text = item.content
holder?.containerFrameLayout.setOnClickListener { listener.onItemClick(position) } }
override fun getItemCount(): Int { return items.count() }
