interface MyAdapterListener {
    void onItemClick(int position);
}

public class MyAdapter extends RecyclerView.Adapter<MyAdapter.ViewHolder> {

    // MARK: - Properties
    private ArrayList<ItemModel> items;

    // MARK: - Constructor
    public MyAdapter(ArrayList<ItemModel> items) {
        this.items = items;
    }

    // MARK: - Recycler.Adapter Methods
    @NonNull
    @Override
    public MyAdapter.ViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {
        View view = LayoutInflater
                .from(parent.getContext())
                .inflate(R.layout.item_list, parent, false);
        return new MyAdapter.ViewHolder(view);
    }

    @Override
    public void onBindViewHolder(@NonNull MyAdapter.ViewHolder holder, int position) {
        ItemModel item = items.get(position);
        // Example
        // holder.titleTextView.setText(item.getTitle());
    }

    @Override
    public int getItemCount() {
        return items.size();
    }

    // MARK: - ViewHolder
    class ViewHolder extends RecyclerView.ViewHolder {

        // MARK: - Public Properties
        public FrameLayout containerFrameLayout;
        public ImageView thumbnailImageView;
        public TextView titleTextView;
        public TextView contentTextView;

        // MARK: - Constructor
        public ViewHolder(View itemView) {
            super(itemView);
            this.containerFrameLayout = itemView.findViewById(R.id.containerFrameLayout);
            this.thumbnailImageView = itemView.findViewById(R.id.thumbnailImageView);
            this.titleTextView = itemView.findViewById(R.id.titleTextView);
            this.contentTextView = itemView.findViewById(R.id.contentTextView);
        }
    }
}

